import React, {useEffect, useState} from 'react';
import {Col, ListGroup} from 'react-bootstrap';
import moment from "moment";
import axios from "axios";
import SportsStyle from './Sports.module.css';

export default function SportsEvent(){
    //dummy data for events list
    const [events,SetEvents] = useState();
    useEffect(()=>{
        axios.get("./sportsEvents.json",).then(response =>{
            SetEvents(response);
        })
    })
    return(
        <div>
            <ListGroup>
                {events.map((item,index ) => {
                    var date =moment(item.date[0]).format("MMM Do, h:mm a");
                    return(
                        <ListGroup.Item className={SportsStyle.listItem} action href="#link1" id={item.id}>
                            <Col xs={8} md={5} >
                                <img className={SportsStyle.eventImg} src={item.eventImg}  alt={'Image failed to load'}/>
                            </Col>
                            <Col  xs={10} md={7}>
                                <div className={SportsStyle.eventInfo}>
                                    <h5>{item.name}</h5>
                                    <p className={SportsStyle.eventDate}>{date}</p>
                                    <p className={SportsStyle.eventLocation}>Location:{item.location}</p>
                                    <p className={SportsStyle.eventPrice}>Starts at:NZ${item.price}</p>
                                </div>
                            </Col>
                        </ListGroup.Item>
                    )

                })}
                {/*<ListGroup.Item action href="#link1">*/}
                {/*    Link 1*/}
                {/*</ListGroup.Item>*/}
            </ListGroup>
        </div>
    )
}
