import React, {useEffect, useState} from 'react';
import axios from "axios";
import DetailsStyle from './Details.module.css';
import {Col} from "react-bootstrap";
import SportsStyle from "../Sports.module.css";

export default function SportEventsDetails(props){
    const [isLoading,setLoading] = useState(true);
    const[eventDetail,setDetails] = useState();
    useEffect(()=>{
        axios.get("../sportsEvents.json",).then(response =>{
            const eventId = props.location.search.slice(4);
            for(let n in response.data) {
                var ids = response.data[n].eventId;
                console.log(ids);
                console.log(eventId);
                if (eventId == ids){
                   setDetails(response.data[n]);
                   setLoading(false);
                   console.log(response.data[n]);
                   console.log(eventDetail)
                }
            }

        });
   },[])

    if (isLoading){
        return(
            <div>loading</div>
        )

    }
    return(
        <div>
            <div className={DetailsStyle.container}>
                <div className={DetailsStyle.eventInfo}>
                    <Col xs={8} md={5} src={eventDetail.eventImg} >
                        <img alt={'Image failed to load'}/>
                    </Col>
                    <Col xs={10} md={7}>
                        <h5>{eventDetail.eventName}</h5>
                    </Col>
                </div>
            </div>
        </div>
    )
}
