import React, {useEffect, useState} from 'react';
import {Col, Dropdown, Form, ListGroup} from "react-bootstrap";
import OrderStyle from './Order.module.css';
import axios from "axios";
import moment from "moment";

export default function PlaceOrder(props){
    const [isLoading,setLoading] = useState(true);
    const [details,setDetails] =useState();
    useEffect(()=>{
        axios.get("../sportsEvents.json",).then(response =>{

            const eventId = props.eventDetails.eventId;

            for(let n in response.data) {
                var ids = response.data[n].eventId;
                if (eventId == ids){
                    setDetails(response.data[n]);
                    setLoading(false);
                }
            }

        });
    },[])
    if (isLoading){
        return(
            <div>loading</div>
        )
    }
    // var date
    // var eventDates = details.eventDate;
    // for(let m in details.eventDate){
    //     var eventDate =moment(details.eventDate[m]).format("MMM Do, h:mm a");
    //     date = <span>{eventDate}</span>
    // }
    return(
        <div className={OrderStyle.orderContainer}>
            <Col xs={10} md={7}>
                <div className={OrderStyle.eventInfo}>
                    <div className={OrderStyle.eventTitle}>
                        <p>{details.eventName}</p>
                        <p style={{color:"grey",fontSize:"14px"}}>{moment(details.eventDate[0]).format("MMM Do, h:mm a")}</p>
                    </div>
                    <div className={OrderStyle.eventList}>
                        <ListGroup>
                            {details.eventDate.map((item,index ) => {
                                console.log(item);
                                //var eventDate =moment(item.eventDate).format("MMM Do, h:mm a");
                                return(
                                    <ListGroup.Item className={OrderStyle.listItem}>
                                        {/*<Form>*/}
                                            <Col  xs={11} md={8}>
                                                <div className={OrderStyle.itemInfo}>
                                                    <p>{details.eventName}</p>
                                                    <p>{moment(item).format("MMM Do, h:mm a")}</p>
                                                    <p className={OrderStyle.itemPrice}>NZ${details.ticketPrice}</p>
                                                </div>
                                            </Col>
                                            <Col xs={6} md={4}>
                                                <Form.Control
                                                    as="select"
                                                    custom
                                                    style={{width:"80px"}}
                                                    //onChange={this.onChangeColor.bind(this)}
                                                >
                                                    <option value="1">1</option>
                                                    <option value="2">2</option>
                                                    <option value="3">3</option>
                                                    <option value="4">4</option>
                                                    <option value="5">5</option>
                                                </Form.Control>
                                            </Col>
                                        {/*</Form>*/}

                                    </ListGroup.Item>

                                )
                            })}
                        </ListGroup>

                    </div>

                </div>
            </Col>
            <Col xs={8} md={5}>
                <div>

                </div>
            </Col>

        </div>
    )
}
