import React from 'react';
import {Col, ListGroup} from 'react-bootstrap';
import SportsStyle from './Sports.module.css';
import moment from "moment";

export default function SportsEvent(){
    //dummy data for events list
    const events = [
        { id: 1, name: "Rise of Champions",date:["12/02/2022"],price: [38.02],eventImg:"https://img.evbuc.com/https%3A%2F%2Fcdn.evbuc.com%2Fimages%2F144220057%2F567194726741%2F1%2Foriginal.20210811-110608?w=512&auto=format%2Ccompress&q=75&sharp=10&rect=13%2C500%2C1598%2C799&s=4b5a93b459e5f9d3749b22cacf02b1e8",location:"165 Lake Road Rotorua, Bay of Plenty 3015"},
        { id: 2, name: "Suburbs Golf Masters",date:["21/11/2021","02/01/2022"],price: [120],eventImg:"https://img.evbuc.com/https%3A%2F%2Fcdn.evbuc.com%2Fimages%2F147865889%2F459776611836%2F1%2Foriginal.20210913-031945?w=720&auto=format%2Ccompress&q=75&sharp=10&rect=0%2C11%2C2848%2C1424&s=6e1d15d6702657004911647d15edf4fb",location:""},
        { id: 3, name: "Got To Get Out Snow Club BEGINNER+: Mt Ruapehu (Long weekend!)",date:["2021/10/22"], price: [249.65,290.98],eventImg:"https://img.evbuc.com/https%3A%2F%2Fcdn.evbuc.com%2Fimages%2F139724281%2F229749391275%2F1%2Foriginal.20210624-080305?w=800&auto=format%2Ccompress&q=75&sharp=10&rect=0%2C94%2C8518%2C4259&s=0a3a780916f73681ee40d21b8ec09427",location:"archie cafe • Auckland, Auckland" },
    ];
    return(
        <div>
            <ListGroup>
                {events.map((item,index ) => {
                    var date =moment(item.date[0]).format("MMM Do YY");
                    return(
                        <ListGroup.Item className={SportsStyle.listItem} action href="#link1" id={item.id}>
                            <Col xs={8} md={5} >
                                <img className={SportsStyle.eventImg} src={item.eventImg}  alt={'Image failed to load'}/>
                            </Col>
                            <Col  xs={10} md={7}>
                                <div className={SportsStyle.eventInfo}>
                                    <h5>{item.name}</h5>
                                    <p className={SportsStyle.eventDate}>{date}</p>
                                </div>
                            </Col>
                        </ListGroup.Item>
                    )

                })}
                {/*<ListGroup.Item action href="#link1">*/}
                {/*    Link 1*/}
                {/*</ListGroup.Item>*/}
            </ListGroup>,
        </div>
    )
}
