import React, {useEffect, useState} from "react";
import axios from "axios";
import QRCode from 'qrcode.react';

let eventId;
let eventImg;
export default function ETicket(props){
    const [isLoading,setLoading] = useState(true);
    const [data,setData] = useState();
    eventId = props.location.search.slice(4);
    useEffect(()=>{
        axios.get("../sportsEvents.json",).then(response =>{
            for(let n in response.data) {
                var ids = response.data[n].eventId;
                if (eventId == ids){
                    setData(response.data[n]);
                    eventImg = data[n].eventImg;
                    setLoading(false);
                }
            }
            console.log(data);

        });
    },[])
    if (isLoading){
        return(
            <div>loading</div>
        )
    }

    return(
        <div>
            <QRCode
                value='https://www.baidu.com/'// 生成二维码的内容
                size={300} // 二维码的大小
                fgColor="#000000" // 二维码的颜色
                imageSettings={{ // 中间有图片logo
                    src:{data},
                    height: 60,
                    width: 60,
                    excavate: true
                }}
            />
        </div>
    )
}
